  <script>
    var jumlah = [];
    $.post("<?= base_url('admin/dashboard/service') ?>",
      function(data){
        var obj = JSON.parse(data);
        $.each(obj, function(test,item){
          jumlah.push(item.a);
        });
      });
      $.post("<?= base_url('admin/dashboard/ambulance') ?>",
      function(data){
        var obj = JSON.parse(data);
        $.each(obj, function(test,item){
          jumlah.push(item.c);
        });
      });
    var ctx = document.getElementById("statistik").getContext('2d');
    var myChart = new Chart(ctx, {
      type: 'pie',
      data: {
        datasets: [{
          data: jumlah,
          backgroundColor: [
            '#63ed7a',
            '#fc544b',
          ],
        }],
        labels: [
          'Penyedia Jasa',
          'Sewa Ambulance'
        ],
      },
      options: {
        responsive: true,
        legend: {
          display: false,
          position: 'bottom',
        },
      }
    });
  </script>

  <script>
    var x = [];
    var y = [];
    $.post("<?= base_url('admin/dashboard/service_') ?>",
      function(data){
        var obj = JSON.parse(data);
        $.each(obj, function(test,item){
          x.push(item.jumlah_jual);
          y.push(item.nama_layanan);
        });
      });
    var ctx = document.getElementById("statistik-produk").getContext('2d');
    var myChart = new Chart(ctx, {
      type: 'pie',
      data: {
        datasets: [{
          data: x,
          backgroundColor: [
            '#abcdef',
            '#c2cc3b',
            '#ff00ff',
            '#ff00ff',
            '#ffa500',
            '#ff7373',
            '#ffd700',
            '#133337',
            '#cbcba9',
            '#696969',
            '#407294',
            '#baebae',
            '#ff4646',
            '#37c8ae',
            '#327345',
            '#c1ffc1',
            '#efcdcd',
            '#e3dab0',
            '#afd2c3',
            '#777777',
          ],
        }],
        labels: y,
      },
      options: {
        responsive: true,
        legend: {
          display: false,
          position: 'bottom',
        },
      }
    });
  </script>
